import { Image } from 'react-native'

export default function Room( {dir, moveLocation, room, placeRoom, row, col, currLocation, setLocation, imgSize} ) {


    function goToNextRoom(row, col) {
        let dir;
        if (currLocation[0] + 1 === row && currLocation[1] === col) dir = 'down'
        if (currLocation[0] - 1 === row && currLocation[1] === col) dir = 'up'
        if (currLocation[0] === row && currLocation[1] + 1 === col) dir = 'right'
        if (currLocation[0] === row && currLocation[1] - 1 === col) dir = 'left'
        moveLocation(row, col, dir)
    }

    let rotation = '0deg'
    if (imgSize == null) imgSize = '100px';
    if (dir === 'right') rotation = '90deg'
    if (dir === 'left') rotation = '90deg'
    if (dir === 'right') rotation = '90deg'

    function getRoom() {

        if (room != null) {



            if (dir === 'right') {
                if (currLocation[0] === row && currLocation[1] === col) {
                    return <div onClick={()=>{goToNextRoom(row, col)}} >
                            <Image style={{transform: 'rotate(90deg)',width: `${imgSize}`, height: `${imgSize}`, borderTop: '2px solid green', borderLeft: '2px solid green', borderBottom: '2px solid green', borderRight: '2px solid green'}} source={room} />
                        </div>
                }
                return <div onClick={()=>{goToNextRoom(row, col)}} >
                            <Image style={{transform: 'rotate(90deg)',width: `${imgSize}`, height: `${imgSize}`, borderTop: ' .1px solid    #A0A2AE',     borderRight: ' .1px solid    #A0A2AE'}} source={room} />
                        </div>
            } else if (dir === 'left') {
                if (currLocation[0] === row && currLocation[1] === col) {
                    return <div onClick={()=>{goToNextRoom(row, col)}} >
                            <Image style={{transform: 'rotate(-90deg)',width: `${imgSize}`, height: `${imgSize}`, borderTop: '2px solid green', borderLeft: '2px solid green', borderBottom: '2px solid green', borderRight: '2px solid green'}} source={room} />
                        </div>
                }
                return <div onClick={()=>{goToNextRoom(row, col)}} >
                            <Image style={{transform: 'rotate(-90deg)',width: `${imgSize}`, height: `${imgSize}`, borderTop: ' .1px solid    #A0A2AE',     borderRight: ' .1px solid    #A0A2AE'}} source={room} />
                        </div>
            } else if (dir === 'down') {
                if (currLocation[0] === row && currLocation[1] === col) {
                    return <div onClick={()=>{goToNextRoom(row, col)}} >
                            <Image style={{transform: 'rotate(180deg)',width: `${imgSize}`, height: `${imgSize}`, borderTop: '2px solid green', borderLeft: '2px solid green', borderBottom: '2px solid green', borderRight: '2px solid green'}} source={room} />
                        </div>
                }
                return <div onClick={()=>{goToNextRoom(row, col)}} >
                            <Image style={{transform: 'rotate(180deg)',width: `${imgSize}`, height: `${imgSize}`, borderTop: ' .1px solid    #A0A2AE',     borderRight: ' .1px solid    #A0A2AE'}} source={room} />
                        </div>
            }


            if (currLocation[0] === row && currLocation[1] === col) {
                return <div onClick={()=>{goToNextRoom(row, col)}} >
                        <Image style={{width: `${imgSize}`, height: `${imgSize}`, borderTop: '2px solid green', borderLeft: '2px solid green', borderBottom: '2px solid green', borderRight: '2px solid green'}} source={room} />
                    </div>
            }
            return <div onClick={()=>{goToNextRoom(row, col)}} >
                        <Image style={{width: `${imgSize}`, height: `${imgSize}`, borderTop: ' .1px solid    #A0A2AE',     borderRight: ' .1px solid    #A0A2AE'}} source={room} />
                    </div>







        } else {
            return <div onClick={()=>{placeRoom(row, col)}} >
                        <div style={{width: `${imgSize}`, height: `${imgSize}`}} ></div>
                    </div>
        }

    }

    return (
        <div>{getRoom()}</div>

    )



}
