import { Image } from 'react-native'

export default function Room( {dir, moveLocation, room, placeRoom, row, col, currLocation, setLocation} ) {





    function getRoom() {

        if (room != null) {



            if (dir === 'right') {
                if (currLocation[0] === row && currLocation[1] === col) {
                    return <div onClick={()=>{moveLocation(row, col)}} >
                            <Image style={{width: '100px', height: '100px', borderTop: '2px solid green', borderLeft: '2px solid green', borderBottom: '2px solid green', borderRight: '2px solid green'}} source={room} />
                        </div>
                }
                return <div onClick={()=>{moveLocation(row, col)}} >
                            <Image style={{width: '100px', height: '100px', borderTop: '1px solid white', borderLeft: '1px solid white', borderBottom: '1px solid white', borderRight: '1px solid white'}} source={room} />
                        </div>
            } else if (dir === 'left') {
                if (currLocation[0] === row && currLocation[1] === col) {
                    return <div onClick={()=>{moveLocation(row, col)}} >
                            <Image style={{width: '100px', height: '100px', borderTop: '2px solid green', borderLeft: '2px solid green', borderBottom: '2px solid green', borderRight: '2px solid green'}} source={room} />
                        </div>
                }
                return <div onClick={()=>{moveLocation(row, col)}} >
                            <Image style={{width: '100px', height: '100px', borderTop: '1px solid white', borderLeft: '1px solid white', borderBottom: '1px solid white', borderRight: '1px solid white'}} source={room} />
                        </div>
            } else if (dir === 'down') {
                if (currLocation[0] === row && currLocation[1] === col) {
                    return <div onClick={()=>{moveLocation(row, col)}} >
                            <Image style={{width: '100px', height: '100px', borderTop: '2px solid green', borderLeft: '2px solid green', borderBottom: '2px solid green', borderRight: '2px solid green'}} source={room} />
                        </div>
                }
                return <div onClick={()=>{moveLocation(row, col)}} >
                            <Image style={{width: '100px', height: '100px', borderTop: '1px solid white', borderLeft: '1px solid white', borderBottom: '1px solid white', borderRight: '1px solid white'}} source={room} />
                        </div>
            }


            if (currLocation[0] === row && currLocation[1] === col) {
                return <div onClick={()=>{moveLocation(row, col)}} >
                        <Image style={{width: '100px', height: '100px', borderTop: '2px solid green', borderLeft: '2px solid green', borderBottom: '2px solid green', borderRight: '2px solid green'}} source={room} />
                    </div>
            }
            return <div onClick={()=>{moveLocation(row, col)}} >
                        <Image style={{width: '100px', height: '100px', borderTop: '1px solid white', borderLeft: '1px solid white', borderBottom: '1px solid white', borderRight: '1px solid white'}} source={room} />
                    </div>







        } else {
            return <div onClick={()=>{placeRoom(row, col)}} >
                        <Image style={{width: '100px', height: '100px', borderTop: '1px solid white', borderLeft: '1px solid white', borderBottom: '1px solid white', borderRight: '1px solid white'}} source={room} />
                    </div>
        }

    }

    return (
        <div>{getRoom()}</div>

    )



}
