import Room from "../rooms/Room"
import { room1 } from "./../../src/image/index"
import { room2 } from "./../../src/image/index"
import { room3 } from "./../../src/image/index"
import { room4 } from "./../../src/image/index"
import { room5 } from "./../../src/image/index"
import { room6 } from "./../../src/image/index"
import { room7 } from "./../../src/image/index"
import { room8 } from "./../../src/image/index"
import { room9 } from "./../../src/image/index"
import { room10 } from "./../../src/image/index"
import { room11 } from "./../../src/image/index"
import { room12 } from "./../../src/image/index"
import { room13 } from "./../../src/image/index"
import { room14 } from "./../../src/image/index"
import { room15 } from "./../../src/image/index"
import { room16 } from "./../../src/image/index"
import { room17 } from "./../../src/image/index"
import { room18 } from "./../../src/image/index"
import { room19 } from "./../../src/image/index"
import { room20 } from "./../../src/image/index"

import { useState, useEffect } from "react"

export default function Board({roomInput, setRoomInput, currRoomObject, setRoomObject, mapSize, map, setMap}) {

    const roomObject = {
        room1: {left: "closed",right: "closed",up: "open"},room2: {left: "closed",right: "door",up: "closed"},room3: {left: "closed",right: "closed",up: "closed"},room4: {left: "closed",right: "open",up: "open"},room5: {left: "open",right: "closed",up: "closed"},
        room6: {left: "open",right: "door",up: "closed"},room7: {left: "door",right: "closed",up: "open"},room8: {left: "open",right: "closed",up: "door"},room9: {left: "closed",right: "door",up: "closed"},room10: {left: "closed",right: "closed",up: "open"},
        room11: {left: "open",right: "closed",up: "open"},room12: {left: "closed",right: "closed",up: "open"},room13: {left: "door",right: "open",up: "closed"},room14: {left: "closed",right: "closed",up: "open"},room15: {left: "closed",right: "open",up: "open"},
        room16: {left: "closed",right: "open",up: "closed"},room17: {left: "open",right: "closed",up: "door"},room18: {left: "door",right: "door",up: "closed"},room19: {left: "closed",right: "closed",up: "open"},room20: {left: "closed",right: "open",up: "open"}
    }

    let nextRoom;





    const [currLocation, setLocation] = useState([entrance[0] + 1, entrance[1]])

    const [shiftLR, setShiftLR] = useState(0)
    const [shiftUD, setShiftUD] = useState(-200)
    useEffect(() => {
        placeRoom(entrance[0], entrance[1])
    }, []);

    function getRandomRoom(row, col, dir) {
        const roomArray = [room1, room2, room3, room4, room5, room6, room7, room8, room9, room10, room11, room12, room13, room14, room15, room16, room17, room18, room19, room20]
        let randomNumber;
        if (roomInput === '') {
            randomNumber = Math.floor(Math.random()*roomArray.length)
        } else {
            randomNumber = roomInput - 1;
        }
        setRoomInput('')
        let randomRoom = roomArray[randomNumber];
        currRoomObject[`${row}${col}`] = roomObject[`room${randomNumber + 1}`]
        let left = currRoomObject[`${row}${col}`].left
        let right = currRoomObject[`${row}${col}`].right
        let up = currRoomObject[`${row}${col}`].up
        currRoomObject[`${row}${col}`].img = randomRoom
        currRoomObject[`${row}${col}`].place = Object.keys(currRoomObject).length - 1;
        if (dir === "right") {
            currRoomObject[`${row}${col}`].up = left;
            currRoomObject[`${row}${col}`].left = 'open';
            currRoomObject[`${row}${col}`].down = right;
            currRoomObject[`${row}${col}`].right = up;
            currRoomObject[`${row}${col}`].dir = '90deg'
        } else if (dir === "left") {
            currRoomObject[`${row}${col}`].up = right;
            currRoomObject[`${row}${col}`].left = up;
            currRoomObject[`${row}${col}`].down = left;
            currRoomObject[`${row}${col}`].right = 'open';
            currRoomObject[`${row}${col}`].dir = '-90deg'
        } else if (dir === "down") {
            currRoomObject[`${row}${col}`].up = 'open';
            currRoomObject[`${row}${col}`].left = right;
            currRoomObject[`${row}${col}`].down = up;
            currRoomObject[`${row}${col}`].right = left;
            currRoomObject[`${row}${col}`].dir = '180deg';
        } else {
            currRoomObject[`${row}${col}`].down = 'open';
            currRoomObject[`${row}${col}`].dir = '0deg';
        }
        nextRoom = currRoomObject[`${row}${col}`]
        console.log(currRoomObject)
        setRoomObject(currRoomObject)
        return {img: randomRoom, dir: dir}
    }

    function legalNewRoom(row, col) {
        if (currRoomObject[`${currLocation[0]}${currLocation[1]}`] == null) return true
        if ((currLocation[0] + 1 === row && currLocation[1] === col && (currRoomObject[`${currLocation[0]}${currLocation[1]}`]['down'] === 'open' || currRoomObject[`${currLocation[0]}${currLocation[1]}`]['down'] === 'door')) ||
            (currLocation[0] - 1 === row && currLocation[1] === col && (currRoomObject[`${currLocation[0]}${currLocation[1]}`]['up'] === 'open' || currRoomObject[`${currLocation[0]}${currLocation[1]}`]['up'] === 'door')) ||
            (currLocation[0] === row && currLocation[1] + 1 === col && (currRoomObject[`${currLocation[0]}${currLocation[1]}`]['right'] === 'open' || currRoomObject[`${currLocation[0]}${currLocation[1]}`]['right'] === 'door')) ||
            (currLocation[0] === row && currLocation[1] - 1 === col && (currRoomObject[`${currLocation[0]}${currLocation[1]}`]['left'] === 'open' || currRoomObject[`${currLocation[0]}${currLocation[1]}`]['left'] === 'door'))) {
                if (map[row][col] === '') return true
        }
        return false;
    }

    function placeRoom(row, col) {
        if (legalNewRoom(row, col) === false) return 
        let newMap = [...map]
        let dir;
        if (currLocation[0] + 1 === row && currLocation[1] === col) dir = 'down'
        if (currLocation[0] - 1 === row && currLocation[1] === col) dir = 'up'
        if (currLocation[0] === row && currLocation[1] + 1 === col) dir = 'right'
        if (currLocation[0] === row && currLocation[1] - 1 === col) dir = 'left'
        newMap[row][col] = getRandomRoom(row, col, dir);
        setMap(newMap);
        moveLocation(row, col, dir)
    }

    function moveLocation(row, col, dir) {
        let currentRoom;
        if (currRoomObject[`${currLocation[0]}${currLocation[1]}`] != null) currentRoom = currRoomObject[`${currLocation[0]}${currLocation[1]}`] 
        if (currRoomObject[`${row}${col}`] != null) nextRoom = currRoomObject[`${row}${col}`]
        if (currentRoom != null) {
           if (dir === 'up') {
                if ((nextRoom.down !== 'open' && nextRoom.down !== 'door') || (currentRoom.up !== 'open' && currentRoom.up !== 'door')) {
                    return
                }
            } else if (dir === 'down') {
                if (nextRoom.up !== 'open' && nextRoom.up !== 'door' || (currentRoom.down !== 'open' && currentRoom.down !== 'door')) {
                    return
                }
            } else if (dir === 'left') {
                if (nextRoom.right !== 'open' && nextRoom.right !== 'door' || (currentRoom.left !== 'open' && currentRoom.left !== 'door')) {
                    return
                }
            } else if (dir === 'right') {
                if (nextRoom.left !== 'open' && nextRoom.left !== 'door' || (currentRoom.right !== 'open' && currentRoom.right !== 'door')) {
                    return
                }
            } 
        } 

        if (row === currLocation[0] && col === currLocation[1]) return
        if ((currLocation[0] + 1 === row && currLocation[1] === col) ||
            (currLocation[0] - 1 === row && currLocation[1] === col) ||
            (currLocation[0] === row && currLocation[1] + 1 === col) ||
            (currLocation[0] === row && currLocation[1] - 1 === col)) {
               let newLocation = [row, col]
                console.log(dir)
                if (dir === 'down') setShiftUD(shiftUD - 200)
                if (dir === 'up') setShiftUD(shiftUD + 200)
                if (dir === 'left') setShiftLR(shiftLR + 200)
                if (dir === 'right') setShiftLR(shiftLR - 200)
                setLocation(newLocation) 
        }

    }

    return (
        <div style={{overflow: 'hidden', backgroundColor: '#F2DA90', width: '100%', height: '100%', display: 'flex', justifyContent: 'center', alignContent: 'center', alignItems: 'center'}}>
            <div style={{marginLeft:`${shiftLR}px`,marginTop:`${shiftUD}px`,display: 'grid', gridTemplateColumns: `repeat(${mapSize}, 100px)`, gridTemplateRows: `repeat(${mapSize}, 100px)`}} >
                {map.map((row, idx) => {
                    return row.map((column, idx2) => {
                        if (map[idx][idx2] !== '') {
                            return <Room key={`${idx} + ${idx2}`} dir={map[idx][idx2].dir} room={map[idx][idx2].img} row={idx} col={idx2} currLocation={currLocation} setLocation={setLocation} moveLocation={moveLocation} />
                        } else {
                            return <Room key={`${idx} + ${idx2}`} placeRoom={placeRoom} row={idx} col={idx2} currLocation={currLocation} setLocation={setLocation} />
                        }
                    })
                })}
            </div>
        </div>
    )


}
