import Room from "../rooms/Room"
import { room1 } from "./../../src/image/index"
import { room2 } from "./../../src/image/index"
import { room3 } from "./../../src/image/index"
import { room4 } from "./../../src/image/index"
import { room5 } from "./../../src/image/index"
import { room6 } from "./../../src/image/index"
import { room7 } from "./../../src/image/index"
import { room8 } from "./../../src/image/index"
import { room9 } from "./../../src/image/index"
import { room10 } from "./../../src/image/index"

import { useState, useEffect } from "react"

export default function Board() {



    let grid = new Array()
    for (let i = 0; i < 5; i++) {
        grid.push([])
    }
    grid.forEach(row => {
        for (let i = 0; i < 5; i++) {
            row.push('')
        }
    })

    const [map, setMap] = useState(grid)
    const [currLocation, setLocation] = useState([Math.floor(grid.length - 1), Math.floor(grid[grid.length - 1].length / 2)])

    const entrance = [Math.floor(grid.length - 1), Math.floor(grid[grid.length - 1].length / 2)]
    console.log(entrance)
    console.log(grid)


    useEffect(() => {
        console.log("using the effect!")
        placeRoom(entrance[0], entrance[1])
    }, []);

    function getRandomRoom() {
        const roomArray = [room1, room2, room3, room4, room5, room6, room7, room8, room9, room10]
        let roomObject = {
            room1: {
                left: "closed",
                right: "closed",
                up: "open"
            },
            room2: {
                left: "closed",
                right: "door",
                up: "open"
            },
            room3: {
                left: "closed",
                right: "closed",
                up: "closed"
            },
            room4: {
                left: "closed",
                right: "open",
                up: "open"
            },
            room5: {
                left: "open",
                right: "closed",
                up: "closed"
            },
            room6: {
                left: "open",
                right: "door",
                up: "closed"
            },
            room7: {
                left: "door",
                right: "closed",
                up: "open"
            },
            room8: {
                left: "open",
                right: "closed",
                up: "door"
            },
            room9: {
                left: "closed",
                right: "door",
                up: "closed"
            },
            room10: {
                left: "closed",
                right: "closed",
                up: "open"
            },
        }

        // let room = 
        // if (room)
        return roomArray[Math.floor(Math.random()*roomArray.length)];
    }

    function legalNewRoom(row, col) {
        if ((currLocation[0] + 1 === row && currLocation[1] + 1 === col) ||
        (currLocation[0] - 1 === row && currLocation[1] - 1 === col)
        (currLocation[0] - 1 === row && currLocation[1] - 1 === col)) {

        }
    }

    function placeRoom(row, col) {
        if (legalNewRoom(row, col) === false) return 
        let newMap = [...map]
        newMap[row][col] = getRandomRoom();
        setMap(newMap);
        console.log("hello")
        moveLocation(row, col)
    }

    function moveLocation(row, col) {
        if (row === currLocation[0] && col === currLocation[1]) return
        let newLocation = [row, col]
        setLocation(newLocation)
        console.log("moving..")
    }

    return (
        <div style={{overflow: 'hidden', backgroundColor: 'black', width: '100%', height: '90%', position: "absolute", display: 'flex', justifyContent: 'center', alignContent: 'center', alignItems: 'center'}}>
            <div style={{display: 'grid', gridTemplateColumns: 'repeat(5, 100px)', gridTemplateRows: 'repeat(5, 100px)'}} >
                {map.map((row, idx) => {
                    return row.map((column, idx2) => {
                        if (map[idx][idx2] !== '') {
                            return <Room key={`${idx} + ${idx2}`} room={map[idx][idx2]} row={idx} col={idx2} currLocation={currLocation} setLocation={setLocation} moveLocation={moveLocation} />
                        } else {
                            return <Room key={`${idx} + ${idx2}`} placeRoom={placeRoom} row={idx} col={idx2} currLocation={currLocation} setLocation={setLocation} />
                        }
                    })
                })}
            </div>
        </div>
    )


}
