export default function FindModal({ setFindModal, findDetails }) {



    return (
        <div onClick={()=>{setFindModal(false)}} style={{position: 'absolute' , top: '0px', width: '100vw', height: '100vh', display: 'flex', justifyContent: 'center', alignItems: 'center', backgroundColor: 'rgba(0,0,0, .7)', zIndex: '99999'}} >
            <div>{findDetails.time > 0 ? `+${findDetails.time} hours` : null}</div>
            <div style={{color: 'white', fontSize: '25px'}} >{findDetails.details}</div>
        </div>
    )


}
