import { useEffect, useRef } from 'react';
import dice_initialize from './dice/main';

var __html = require('./dice/diceHTML');
var template = { __html: __html };

export default function Dice() {

    const divRef = useRef();

    useEffect(() => {
        dice_initialize(document.body)
        const fragment = document.createRange().createContextualFragment(__html);
        divRef.current.append(fragment);
      }, []);

    return (
        <div ref={divRef} />
    );
  }
