import { StatusBar } from 'expo-status-bar';
import React from 'react';
import Room from './components/rooms/Room';
import Board from './components/board/Board';
import { useState } from 'react';
import MapModal from './components/mapModal/MapModal';

export default function App() {


  const [roomInput, setRoomInput] = useState('')
  const [mapModal, setMapModal] = useState(false)

  function setRoomNumber(value) {
    setRoomInput(value)
  }



  return (
    <div style={{flexDirection: 'column', display: 'flex', justifyContent: 'center', height: '100%'}} >
      {mapModal ? <MapModal/> : null}
      <div style={{display: 'flex', justifyContent: 'space-around'}} >
        <div style={{display:'flex'}} >
          <div>Room #</div>
          <input style={{width: '20px', marginLeft:'5px'}} onChange={e => setRoomNumber(e.target.value)} value={roomInput} />
        </div>
        <button onClick={()=>{setMapModal(true)}} >Map</button>
      </div>
      <div style={{height: '80%', display: 'flex', justifyContent: 'center', alignContent: 'center', alignItems: 'center'}} >
        <Board roomInput={roomInput} setRoomInput={setRoomInput} />
      </div>
    </div>
  );
}
