import Room from "../rooms/Room"
import { room1 } from "./../../src/image/index"

export default function Board() {

    const grid = new Array(5).fill([])
    grid.forEach(row => {
        for (let i = 0; i < 5; i++) {
            row.push('')
        }
    })


    console.log(grid)

    return (
        <div style={{overflow: 'hidden', backgroundColor: 'black', width: '100%', height: '90%', position: "absolute"}}>
            <div style={{display: 'grid', gridTemplateColumns: 'repeat(5, 100px)', gridTemplateRows: 'repeat(5, 100px)'}} >
                {grid.map((row, idx) => {
                    return row.map((column, idx2) => {
                        return <Room key={`${idx} + ${idx2}`} />
                    })
                })}
            </div>
        </div>
    )


}
