import { StatusBar } from 'expo-status-bar';
import React from 'react';
import Room from './components/rooms/Room';
import Board from './components/board/Board';
import { useState } from 'react';
import MapModal from './components/mapModal/MapModal';
import DashBoard from './components/dashboard/DashBoard';

export default function App() {


  const [roomInput, setRoomInput] = useState('')
  const [mapModal, setMapModal] = useState(false)
  const [currRoomObject, setRoomObject] = useState({}) 
  const mapSize = 41;
  function setRoomNumber(value) {
    setRoomInput(value)
  }

  let grid = new Array()
    for (let i = 0; i < mapSize; i++) {
        grid.push([])
    }
    grid.forEach(row => {
        for (let i = 0; i < mapSize; i++) {
            row.push('')
        }
    })
    const entrance = [Math.floor(grid.length / 2), Math.floor(grid[grid.length - 1].length / 2)]
    const [map, setMap] = useState(grid)







  return (
    <div style={{ position: 'relative', height: '100vh'}} >
        {mapModal ? <MapModal map={map} mapSize={mapSize} currRoomObject={currRoomObject} setMapModal={setMapModal} /> : null}
      <div style={{flexDirection: 'column', display: 'flex', justifyContent: 'space-around', height: '100%'}} >
        <div style={{height: '30%', display: 'flex', justifyContent: 'space-around', flexDirection: 'column'}} >
          <DashBoard setMapModal={setMapModal} setRoomNumber={setRoomNumber} roomInput={roomInput} />
        </div>
        <div style={{flexDirection: 'column', display: 'flex', justifyContent: 'flex-end', height: '70%'}} >
          <Board entrance={entrance} map={map} setMap={setMap} mapSize={mapSize} currRoomObject={currRoomObject} setRoomObject={setRoomObject} roomInput={roomInput} setRoomInput={setRoomInput} />
        </div>
      </div>
    </div>
  );
}
