import { Image } from 'react-native'

export default function Room( {moveLocation, room, placeRoom, row, col, currLocation, setLocation} ) {





    function getRoom() {

        if (room != null) {
            return <div onClick={()=>{moveLocation(row, col)}} >
                        <Image style={{width: '100px', height: '100px', borderTop: '1px solid white', borderLeft: '1px solid white', borderBottom: '1px solid white', borderRight: '1px solid white'}} source={room} />
                    </div>
        } else {
            if (currLocation[0] === row && currLocation[1] === col) {
                return <div onClick={()=>{placeRoom(row, col)}} >
                        <Image style={{width: '100px', height: '100px', borderTop: '1px solid green', borderLeft: '1px solid green', borderBottom: '1px solid green', borderRight: '1px solid green'}} source={room} />
                    </div>
            }
            return <div onClick={()=>{placeRoom(row, col)}} >
                        <Image style={{width: '100px', height: '100px', borderTop: '1px solid white', borderLeft: '1px solid white', borderBottom: '1px solid white', borderRight: '1px solid white'}} source={room} />
                    </div>
        }

    }

    return (
        <div>{getRoom()}</div>

    )



}
